package io.swagger.model;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.Column;import javax.persistence.Embeddable;@Data@AllArgsConstructor@NoArgsConstructor@Embeddablepublic class Address {    @Column(name = "village",length = 50,nullable = false)    private String village;    @Column(name = "thana",length = 50,nullable = false)    private String thana;    @Column(name = "district",length = 50,nullable = false)    private String district;    @Override    public String toString() {        return village+","+thana+","+district;    }}